if(loadedQuotes)
{
  loadedQuotes.filter((loadedQuote) => {
    return loadedQuote.id !== props.id;
 })
 return loadedQuotes;
}

    }

  return (
    <li className={classes.item}>
      <figure>
        <blockquote>
          <p>{props.text}</p>
        </blockquote>
        <blockquote>
          <p>{props.date}</p>
        </blockquote>
        <figcaption>{props.author}</figcaption>
      </figure>
      <button className='btn' onClick={removeItemHandler}>
        Delete
      </button>
      <button className='btn'>
        Edit
      </button>
        <button
                className='btn'
                  //for="btn-check-2"
                  //text-align="center"
                  onClick={onReviewClick}
                 >
                  Done {props.type}
                </button>
                <div className="p-2 flex-fill bd-highlight" className={isActive ? classes.checked:classes.unchecked}></div>


    </li>
  );
};

