import { Link } from 'react-router-dom';

import classes from './QuoteItem.module.css';

const QuoteItem = (props) => {
  return (
    <li className={classes.item}>
      <figure>
        {/* <blockquote>
          <p>${props.text}</p>
        </blockquote> */}
        <blockquote>
          <p>{props.date}</p>
        </blockquote>
        <figcaption>{props.author}</figcaption>
      </figure>
      <button className='btn'>
        Delete
      </button>
      <button className='btn'>
        Edit
      </button>
      <div class="d-flex bd-highlight">
        <div class="p-2 flex-fill bd-highlight" className={isActive ? classes.checked:classes.unchecked}></div>
        <div class="p-2 flex-fill bd-highlight"><h6>{props.type}</h6>
               {props.message}
               <br className={classes.print}/>
               {pdfCaption(props.downloadPdf) }
               <br />
               {props.submittedOn}</div>
        <div class="p-2  bd-highlight"><button
               class="btn btn-primary"
                  for="btn-check-2"
                  text-align="right"
                  onClick={onReviewClick}
                 >
                  Review {props.type}
                </button></div>
    </div>
    </li>
  );
};

export default QuoteItem;
